mapper.update:
update ${tableName}
instance_id= #{instanceId}, defid= #{defid}, start_user_id= #{startUserId}, code= #{code}, update_time= #{updateTime}, update_id= #{updateId}, start_time= #{startTime}, status = #{status}, act_result = #{actResult},
where ${pkName} =#{id}

mapper.select:
select a.name_ AS taskName,a.CREATE_TIME_ AS createTime, a.id_ AS taskId, a.proc_inst_id_ AS instanceId,fb.defid AS defId, fb.code,fb.act_key AS actKey,fb.bus_id AS busId, (SELECT name FROM extend_act_business ab WHERE fb.act_key=ab.act_key ) AS busName, (SELECT user_name FROM sys_user u WHERE u.id =fb.start_user_Id)AS startUserName, (SELECT user_name FROM sys_user u WHERE u.id =a.ASSIGNEE_ ) AS dealName, (SELECT node_type FROM extend_act_nodeset ns WHERE ns.defid=fb.defid) AS nodeType FROM extend_act_flowbus fb,act_ru_task a WHERE a.proc_inst_id_= fb.instance_id
AND ASSIGNEE_ = #{dealId}
AND fb.code LIKE concat('%',#{code},'%')
AND fb.bus_id = #{busId}
ORDER BY a.CREATE_TIME_ DESC

select a.create_time,a.deal_time,a.bus_id AS busId,a.def_id AS defId,a.instance_id AS instanceId,a.task_id AS taskId, a.task_name AS taskName,a.app_opinion AS remark,fb.code,fb.status AS status,fb.act_key, (SELECT name FROM extend_act_business ab WHERE fb.act_key=ab.act_key ) AS busName, (SELECT user_name FROM sys_user u WHERE u.id =fb.start_user_Id)AS startUserName, (SELECT user_name FROM sys_user u WHERE u.id =a.deal_id ) AS dealName, (SELECT user_name FROM sys_user u WHERE u.id =a.advance_id ) AS advanceName, (SELECT node_type FROM extend_act_nodeset ns WHERE ns.defid=fb.defid) AS nodeType FROM extend_act_flowbus fb,extend_act_tasklog a WHERE a.instance_id= fb.instance_id
AND a.deal_id = #{dealId}
AND fb.code LIKE concat('%',#{code},'%')
AND fb.bus_id = #{busId}
ORDER BY a.deal_time DESC
